<?php	echo "<?xml version='1.0' encoding='utf-8'?>\n";	echo "<hints>";	get_suggestions("horizontal", "/" . $_GET['hpattern'] . "/");	get_suggestions("vertical", "/" . $_GET['vpattern'] . "/");	echo "</hints>";    function get_suggestions($section, $pattern)    {		echo "<$section pattern=\"";		echo $pattern;		echo "\">\n";			if ($fp = fopen("db/normal.txt", "r"))		{			for ($i = 0; $i<100 && !feof($fp); )			{				$line = fgets($fp, 256);				$line = trim($line);				if (preg_match($pattern . "i", $line))				{					echo "<h>$line</h>\n";					$i++;				}			}				fclose($fp);		}		echo "</$section>\n";	}?>